package Arrays;

public class AreaOfCircle {
	
	public static int areaOfSqare(int a) {
		return a*a;
	}
	public static int periOfRectangle(int length,int bradth) {
		return 2*(length+bradth);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Area of sqare ="+areaOfSqare(5));
		System.out.println("Perimeter of Rectangle ="+periOfRectangle(10,8));
	}

}
